	/**
	 * 是不是应该保存,有没有对应的公司
	 * @param	string	$page	网页的html代码
	 **/
	public function checkPageRight($page)
	{
		if($page == false){
			$fp = fopen(PATH_ROOT . 'error.log' , 'a') or die("create file failed\n");
			fwrite($fp , "|{$i} | rbAfter2006 | {$data}| ,返回为false\n");
			fclose($fp);
			return false;
		}
		$this->HtmlParserModel->parseStr($page, array(), "BIG5");
		$lines = $this->HtmlParserModel->find('#ctl00_gvMain_ctl01_lbPageCount');
		if(count($lines) == 1){
			return  true;
		}
		return false;
	}
	/**
	 * 生成对应的香港股市编号
	 */
	public function makeCode()
	{
		/*
		$config['ctl00$rdo_SelectDocType'] = 'rbAfter2006';
		$config['ctl00$rdo_SelectDocType'] = 'rbPrior2006';
		$config['ctl00$sel_tier_2'] = array(159,160,161);
		 */
		$config = array(159,160,161);
		$this->DataBaseModel->setTables('pages');
		for($i = 0;$i <= 99999;$i++){
			$len = 5 - strlen($i);
			while($len --){
				$i = '0' . $i;
			}
			$res = array();
			foreach($config as $data){
				//传入 code 和对应的类型
				$page = $this->getCompanyInfo($i,
					array(
						'ctl00$rdo_SelectDocType' => 'rbAfter2006',
						'ctl00$sel_tier_2' => $data
					)
				);
				if($this->checkPageRight($page)){
					$res[] = array($i,'0',base64_encode($page) , '11500');
				}
			}
			$page = $this->getCompanyInfo( '00010',
				array(
					'ctl00$rdo_SelectDocType' => 'rbPrior2006',
					'ctl00$sel_tier_2' => '-2' , 
					'ctl00$sel_DocTypePrior2006' => 11500,
				)
			);
			if($this->checkPageRight($page)){
				$res[] = array( $i,'0',base64_encode($page) , '11500');
			}
			$this->DataBaseModel->insert(
				array('code' , 'pageId' , 'content' , 'notice'),$res
			) && printf( "insert success");
			die("test");
		}
	}
	
	/**
	 * 在对应的page表里面,解析出来对应的数据
	 *
	 **/
	public function selectPage()
	{
		$data = 'ds';
		if(DEBUG){
			$data = $this->getCompanyInfo();
			$data = array(file_get_contents(PATH_ROOT . 'game.html' , true));
		}
		/*
		$this->DataBaseModel->setTables($this->config['shenpage']);
		$data = $this->DataBaseModel->select(' notice , code ,content');
		$this->DataBaseModel->createTable('data');
		$cnt = 0;
		 */
		$baseUrl = "http://www.hkexnews.hk/";
		foreach ($data  as $page ) {
			$this->HtmlParserModel->parseStr($page, array(), "BIG5");
			$lines = $this->HtmlParserModel->find('#ctl00_gvMain');
			$this->HtmlParserModel->parseStr($lines[0]->value , array() , 'BIG5');
			$lines = $this->HtmlParserModel->find('tr');
			//从每一行td中获取时间和标题，以及对应的下载连接
			$cnt = 0;
			foreach($lines as $line){
				$cnt++;
				$tmpStr = $line->value;
				//$tmpStr = mb_convert_encoding($tmpStr , 'big5' , 'auto');
				// 匹配时间
				//$tmpStr = "sdfa 12/02/3221 sdf";
				preg_match('/\>\s*(\d{2}\s*\/\s*\d{2}\s*\/\s*\d{4}\s*)\s*\</' , $tmpStr , $time);
				if(count($time) != 2){
					var_dump(trim($tmpStr));
					echo "<br/>";
					Debug::output('time is wrong' , E_NOTICE);
					continue;
				} else {
					$tmp = explode('/' , $time[1]);
					$tmp2[0] = $tmp[2];
					$tmp2[1] = $tmp[1];
					$tmp2[2] = $tmp[0];
					$time[1] = implode('-' , $tmp2);
				}
				//$tmpStr = "<a href=\"finalpage/2014-03-07/63646348.PDF\" target=\"new\">平安银行：2013年年度报告摘要</a>";
				//匹配下载连接
				preg_match('/href\=\s*[\'\"]?\s*([^"\']+)/' , $tmpStr , $download);
				if(count($download) != 2){
					var_dump($tmpStr);
					echo "<br/>";
					//如果数据不符合，就提示之后跳过
					Debug::output('download link' , E_NOTICE);
					continue;
				}
				//preg_match('/href\=\"([^\s]*)\"\s+/' , $tmpStr , $download);
				//匹配文件大小
				preg_match('/\>\s*\(\s*(\d*)\s*KB,\s*\s*/' , $tmpStr , $size);
				if(count($size) != 2){
					var_dump($tmpStr);
					echo "<br/>";
					Debug::output('size is wrong' , E_NOTICE);
					continue;
				}
				//$tmpStr = "<a href='finalpage/2008-04-22/38959757.PDF' target='new'>*ST宜地：2007年年度报告（补充后）</a>";
				//匹配标题
				preg_match('/\>\s*([^\>]*)\s*\<\s*\\/a\s*\>/' , $tmpStr , $title);
				if(count($title) != 2){
					var_dump($tmpStr);
					echo "<br/>";
					Debug::output('title is wrong' , E_NOTICE);
				}
				echo $cnt . "<br/>";
				flush();
				$this->DataBaseModel->insert(
					array('time' , 'link' , 'size' , 'title' , 'notice' , 'code'), 
					array(
						array(
							$time[1] ,
						   	$baseUrl . $download[1] , 
							$size[1] ,
						   	$title[1] ,
						   	'000' , '000')
					)
				);
			}
		}
	}

